import React, { useState, useEffect, useContext } from "react";
import { useLoaderData, useNavigate } from "react-router-dom";
import UserService from "../services/UserService";
import AuthContext from "../context/AuthContext";

// Loader para buscar o usuário via API
export async function userLoader({ params }) {
  // Você pode ajustar para pegar o token do localStorage ou contexto, aqui um jeito simples:
  const token = localStorage.getItem("token");
  const service = new UserService(token);
  const user = await service.get(params.userId);
  return { user };
}

function EditUser() {
  const { user } = useLoaderData();
  const { token } = useContext(AuthContext);
  const [form, setForm] = useState({
    name: user.name || "",
    email: user.email || "",
    type: user.type || "",
    password: "", // não preenchido por segurança, usuário deve digitar nova senha se quiser trocar
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    setForm({
      name: user.name || "",
      email: user.email || "",
      type: user.type || "",
      password: "",
    });
  }, [user]);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    // Validação simples
    if (!form.name || !form.email || !form.type) {
      setError("Preencha todos os campos obrigatórios (exceto senha se não quiser alterar).");
      return;
    }

    try {
      const service = new UserService(token);
      const dataToUpdate = { ...form };

      // Se senha está vazia, não envia para não sobrescrever
      if (!dataToUpdate.password) {
        delete dataToUpdate.password;
      }

      await service.update(user.id, dataToUpdate);
      navigate("/users");
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao atualizar usuário");
    }
  }

  return (
    <div>
      <p>Edição de Usuário</p>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div>
        <form onSubmit={handleSubmit}>
          <label>Nome:</label><br />
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
          <br />
          <br />

          <label>Email:</label><br />
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <br />
          <br />

          <label>Tipo:</label><br />
          <input
            type="text"
            name="type"
            value={form.type}
            onChange={handleChange}
            required
          />
          <br />
          <br />

          <label>Senha (deixe vazio para não alterar):</label><br />
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleChange}
          />
          <br />
          <br />

          <button type="submit">Salvar</button>
        </form>
      </div>
    </div>
  );
}

export default EditUser;
