import React, { useState, useContext } from "react";
import UserService from "../services/UserService";
import AuthContext from "../context/AuthContext";
import { useNavigate, useParams } from "react-router-dom";

function EditUser() {
  const { userId } = useParams();
  const { token } = useContext(AuthContext);
  const userService = new UserService(token);
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    email: "",
    type: "",
    password: "",
  });

  const [error, setError] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    // Apenas campos preenchidos
    const dataToUpdate = {};
    Object.entries(form).forEach(([key, value]) => {
      if (value.trim() !== "") {
        dataToUpdate[key] = value.trim();
      }
    });

    if (Object.keys(dataToUpdate).length === 0) {
      setError("Preencha pelo menos um campo para atualizar");
      return;
    }

    try {
      await userService.update(userId, dataToUpdate);
      navigate("/users");
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao atualizar usuário");
    }
  }

  return (
    <div
      style={{
        maxWidth: 400,
        margin: "40px auto",
        padding: "30px 20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        backgroundColor: "#fff",
        boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
        borderRadius: 8,
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 24, color: "#333" }}>
        Edição de Usuário (ID: {userId})
      </h2>

      {error && (
        <p
          style={{
            color: "white",
            backgroundColor: "#e74c3c",
            padding: "12px",
            borderRadius: 6,
            marginBottom: 20,
            fontWeight: "600",
            textAlign: "center",
          }}
        >
          {error}
        </p>
      )}

      <form onSubmit={handleSubmit}>
        {[
          { label: "Nome", name: "name", type: "text", placeholder: "Novo nome" },
          { label: "Email", name: "email", type: "email", placeholder: "Novo email" },
          { label: "Tipo", name: "type", type: "text", placeholder: "Novo tipo" },
          { label: "Senha", name: "password", type: "password", placeholder: "Nova senha" },
        ].map(({ label, name, type, placeholder }) => (
          <div key={name} style={{ marginBottom: 20 }}>
            <label
              htmlFor={name}
              style={{ display: "block", marginBottom: 6, fontWeight: "600" }}
            >
              {label}:
            </label>
            <input
              id={name}
              name={name}
              type={type}
              value={form[name]}
              onChange={handleChange}
              placeholder={placeholder}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: 6,
                border: "1px solid #ccc",
                fontSize: "1rem",
                boxSizing: "border-box",
              }}
            />
          </div>
        ))}

        <button
          type="submit"
          style={{
            width: "100%",
            padding: "12px",
            backgroundColor: "#17a2b8",
            border: "none",
            borderRadius: 8,
            color: "white",
            fontWeight: "700",
            fontSize: "1.1rem",
            cursor: "pointer",
            transition: "background-color 0.3s ease",
          }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = "#117a8b")}
          onMouseLeave={(e) => (e.target.style.backgroundColor = "#17a2b8")}
        >
          Salvar
        </button>
      </form>
    </div>
  );
}

export default EditUser;
