import React, { useState, useContext } from "react";
import AuthContext from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const res = await axios.post(`${process.env.REACT_APP_SERVER_URL}/auth/login`, {
        email,
        password,
      });
      login(res.data.token);
      navigate("/users"); // redireciona após login
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao fazer login");
    }
  }

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{color:"red"}}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>Email:</label><br/>
        <input value={email} onChange={e => setEmail(e.target.value)} type="email" required /><br/>

        <label>Senha:</label><br/>
        <input value={password} onChange={e => setPassword(e.target.value)} type="password" required /><br/>

        <button type="submit">Entrar</button>
      </form>
    </div>
  );
}

export default SignIn;
