import React, { useState, useContext } from "react";
import UserService from "../services/UserService";
import AuthContext from "../context/AuthContext";
import { useNavigate, useParams } from "react-router-dom";

function EditUser() {
  const { userId } = useParams();
  const { token } = useContext(AuthContext);
  const userService = new UserService(token);
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    email: "",
    type: "",
    password: "",
  });

  const [error, setError] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    // Cria um objeto só com os campos que o usuário preencheu (não vazios)
    const dataToUpdate = {};
    Object.entries(form).forEach(([key, value]) => {
      if (value.trim() !== "") {
        dataToUpdate[key] = value.trim();
      }
    });

    if (Object.keys(dataToUpdate).length === 0) {
      setError("Preencha pelo menos um campo para atualizar");
      return;
    }

    try {
      await userService.update(userId, dataToUpdate);
      navigate("/users");
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao atualizar usuário");
    }
  }

  return (
    <div>
      <h2>Edição de Usuário (ID: {userId})</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <label>Nome:</label><br />
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Novo nome"
        />
        <br /><br />

        <label>Email:</label><br />
        <input
          type="email"
          name="email"
          value={form.email}
          onChange={handleChange}
          placeholder="Novo email"
        />
        <br /><br />

        <label>Tipo:</label><br />
        <input
          type="text"
          name="type"
          value={form.type}
          onChange={handleChange}
          placeholder="Novo tipo"
        />
        <br /><br />

        <label>Senha:</label><br />
        <input
          type="password"
          name="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Nova senha"
        />
        <br /><br />

        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default EditUser;
