import React, { useContext, useEffect, useState } from "react";
import AuthContext from "../context/AuthContext";
import UserService from "../services/UserService";
import { useNavigate } from "react-router-dom";

function Users() {
  const { token } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const userService = new UserService(token);

  // Carregar usuários
  async function loadUsers() {
    setLoading(true);
    try {
      const data = await userService.list();
      setUsers(data);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao carregar usuários");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    if (token) loadUsers();
  }, [token]);

  // Deletar usuário
  async function handleDelete(id) {
    if (!window.confirm("Confirma exclusão do usuário?")) return;

    try {
      await userService.delete(id);
      loadUsers(); // Recarrega a lista
    } catch (err) {
      alert(err.response?.data?.error || "Erro ao excluir usuário");
    }
  }

  return (
    <div>
      <h1>Usuários</h1>
      <button onClick={() => navigate("/users/new")}>Adicionar Usuário</button>

      {loading && <p>Carregando...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <ul>
        {users.map((u) => (
          <li key={u.id}>
            {u.name} ({u.email}) - {u.type}{" "}
            <button onClick={() => navigate(`/users/${u.id}`)}>Editar</button>{" "}
            <button onClick={() => handleDelete(u.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Users;
