import React, { useState, useContext } from "react";
import AuthContext from "../context/AuthContext";
import UserService from "../services/UserService";
import { useNavigate } from "react-router-dom";

function UserCreate() {
  const { token } = useContext(AuthContext);
  const userService = new UserService(token);
  const navigate = useNavigate();

  const [form, setForm] = useState({ email: "", name: "", type: "", password: "" });
  const [error, setError] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    if (!form.email || !form.name || !form.type || !form.password) {
      setError("Todos os campos são obrigatórios.");
      return;
    }

    try {
      await userService.create(form);
      navigate("/users");
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao criar usuário");
    }
  }

  return (
    <div>
      <h2>Adicionar Usuário</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>Email:</label><br />
        <input name="email" value={form.email} onChange={handleChange} type="email" required /><br />

        <label>Nome:</label><br />
        <input name="name" value={form.name} onChange={handleChange} required /><br />

        <label>Tipo:</label><br />
        <input name="type" value={form.type} onChange={handleChange} required /><br />

        <label>Senha:</label><br />
        <input name="password" value={form.password} onChange={handleChange} type="password" required /><br />

        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default UserCreate;
